#ifndef DIO_CFG_H_
#define DIO_CFG_H_

/* Description:	Initializing Configurations of all pins in the uC
 */
/*				All Outputs are low by default after initialization
 */
/* Range:		DIO_u8INPUTANALOG,	DIO_u8INPUTFLOATING,
 */
/*				DIO_u8INPUTPULLUP,	DIO_u8INPUTPULLDOWN
 */
/*				DIO_u8DIGITALOUTPUTPUSHPULL_10MHZ,
 */
/*				DIO_u8DIGITALOUTPUTPUSHPULL_2MHZ,
 */
/* 				DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ,
 */
/* 				DIO_u8DIGITALOUTPUTOPENDRAIN_10MHZ,
 */
/* 				DIO_u8DIGITALOUTPUTOPENDRAIN_2MHZ,
 */
/* 				DIO_u8DIGITALOUTPUTOPENDRAIN_50MHZ,
 */
/* 				DIO_u8AFOUTPUTPUSHPULL_10MHZ,
 */
/* 				DIO_u8AFOUTPUTPUSHPULL_2MHZ,
 */
/* 				DIO_u8AFOUTPUTPUSHPULL_50MHZ,
 */
/* 				DIO_u8AFOUTPUTOPENDRAIN_10MHZ,
 */
/* 				DIO_u8AFOUTPUTOPENDRAIN_2MHZ,
 */
/* 				DIO_u8AFOUTPUTOPENDRAIN_50MHZ
 */
// PortA pins
#define DIO_u8PINA0CFG DIO_u8INPUTANALOG
#define DIO_u8PINA1CFG DIO_u8INPUTANALOG
#define DIO_u8PINA2CFG DIO_u8INPUTPULLDOWN
#define DIO_u8PINA3CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA4CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA5CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA6CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA7CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA8CFG DIO_u8AFOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA9CFG DIO_u8AFOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA10CFG DIO_u8INPUTFLOATING
#define DIO_u8PINA11CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA12CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA13CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA14CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINA15CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
// PortB Pins
#define DIO_u8PINB0CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB1CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB2CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB3CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB4CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB5CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB6CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB7CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB8CFG DIO_u8INPUTPULLDOWN
#define DIO_u8PINB9CFG DIO_u8AFOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB10CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB11CFG DIO_u8INPUTPULLUP
#define DIO_u8PINB12CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB13CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB14CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINB15CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
// PortC Pins
#define DIO_u8PINC13CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINC14CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ
#define DIO_u8PINC15CFG DIO_u8DIGITALOUTPUTPUSHPULL_50MHZ

#endif /* DIO_CFG_H_ */
